entry.value:
def flowFile = session.create()
if (!flowFile) return

def countries = ['IT', 'SE']
def years = ['2024', '2023', '2022', '2021']
def aggs = ['hourly']

def builder = new StringBuilder()
countries.each { c ->
    years.each { y ->
        aggs.each { a ->
            builder.append("${c},${y},${a}\n")
        }
    }
}

flowFile = session.write(flowFile, { outputStream ->
    outputStream.write(builder.toString().getBytes("UTF-8"))
} as OutputStreamCallback)

flowFile = session.putAttribute(flowFile, "filename", "combinations.csv")
session.transfer(flowFile, REL_SUCCESS)
